fields.definition:
:root {
  --own-message-bg-color: #00d4c288;
  --own-message-color: #fff;
  --foreign-message-bg-color: #b0c5c5de;
  --foreign-message-color: #444;
  --button-hover-color: #00d4c2cc;
  --button-color: #00d4c288;
}

@font-face {
    font-family: Roboto-Regular;
    src: url(/fonts/Sans/Roboto/Roboto-Regular.ttf);
}

.screen {
    font-family: Roboto-Regular, sans-serif;
    font-size: 24pt;
    color: #fff;
    margin: 12pt;
}

.screen .title {
    font-family: Roboto-Regular, sans-serif;
    font-size: 48pt;
    color: #fff;
    text-align: center;
    margin: 0;
    padding-top: 24pt;
    padding-bottom: 12pt;
}

.screen .imageA {
    height: 25%;
}

.screen .chat {
    height: 100%;
    overflow-y: hidden;
}

.ownChatMessage {
    font-size: 16pt;
    color: var(--own-message-color);
    background-color: var(--own-message-bg-color);
}

.arrowRight {
    border-left-color: var(--own-message-bg-color);
}

.button {
    color: var(--own-message-color);
    background-color: var(--button-color);
    border-color: #fff;
    border-radius: 13px;
    border: none;
}

.button:hover {
   background-color: var(--button-hover-color);
}

.foreignChatMessage {
    font-size: 16pt;
    color: var(--foreign-message-color);
    background-color: var(--foreign-message-bg-color);
    border-radius: 15px;
    padding: 20px;
    margin-right: 20%;
}

.arrowLeft {
    border-right-color: var(--foreign-message-bg-color);
}
