@font-face {
    font-family: Roboto-Regular;
    src: url(/fonts/Sans/Roboto/Roboto-Regular.ttf);
}

.slidelayout {
    display: flex;
    justify-content: space-between;
}

.slideimages {
    order: 2;
}

.slideimage {
    width: 400px;
    margin: 40px 40px 0px 40px;
}

.slideimage > img {
    width: 100%;
    height: auto;
}

.slidecontent {
    position: absolute;
    width: 100%;
    height: 100%;
}

.slidetitle {
    font-family: Roboto-Regular, sans-serif;
    font-size: 72pt;
    color: #000;
    text-align: center;
    margin: 0;
    padding-top: 24pt;
    padding-bottom: 12pt;
}

.slide {
    font-family: Roboto-Regular, sans-serif;
    font-size: 24pt;
    color: #000;
    text-align: center;
    margin: 12pt;
}

.slide p {
}

.slide blockquote {
    color: #888;
    background: #111;
    padding: 0.5em;
    margin: 12pt 0;
}

.slide pre {
    border: 1px solid #333;
    display: block;
    background: #111;
    padding: 0.5em;
}

.slide blockquote p {
    margin: 0;
}

.slide code {
    border: 1px solid #000;
    background: #111;
    color: #777;
    padding: 0 0.25em;
}

.slide pre code {
    border: none;
    background: #111;
    color: #888;
    padding: 0 0;
}

.slide table {
    border-collapse: collapse;
    border-spacing: 0;
    margin-bottom: 0.5em;
    margin-left: auto;
    margin-right: auto;
}

.slide th {
    padding: 6px 13px;
    background: #111;
    color: #888;
}

.slide td {
    padding: 6px 13px;
    text-align: left;
}

.slide table tr {
    background: #222;
    color: #888;
}

.slide table tr:nth-child(2n) {
    background: #111;
    color: #888;
}
